import React from 'react';
import QuoteCard from './QuoteCard';
import SimpsonList from './Components/SimpsonList';

function App() {
  const [showSimsonOnly, setShowSimsonOnly] = React.useState(false);
  const [quoteList, setQuoteList] = React.useState([
  ]);



  function toggleSimsonOnly() {
    setShowSimsonOnly(!showSimsonOnly);
  }

  return (
    <>
      <button onClick={toggleSimsonOnly}>
        Show only Simson : {showSimsonOnly ? 'ON' : 'OFF'}
      </button>
      <div>
        {quoteList
          .filter(
            (quote) => !showSimsonOnly || quote.character.includes('Simpson')
          )
          .map((quote, index) => (
            <QuoteCard key={index} {...quote} />
          ))}
      </div>
      <SimpsonList />
    </>
  );
}

export default App;
