import React from 'react';
import DisplaySimpson from './Components/DisplaySimpson';

function App() {

  function toggleSimsonOnly() {
    setShowSimsonOnly(!showSimsonOnly);
  }

  return (
    <>
      <button onClick={toggleSimsonOnly}>
        Show only Simson : {showSimsonOnly ? 'ON' : 'OFF'}
      </button>
      <DisplaySimpson />
    </>
  );
}

export default App;
